/* eslint-disable no-useless-escape */
import { NextApiRequest, NextApiResponse, NextComponentType, NextPageContext } from "next";
import { logMessage } from "@lib/logger";
import { middleware, cors, sessionExists } from "@lib/middleware";
import {
  GetCommand,
  GetCommandInput,
  UpdateCommand,
  UpdateCommandInput,
} from "@aws-sdk/lib-dynamodb";
import { MiddlewareProps, WithRequired, Responses } from "@lib/types";
import { AccessControlError, createAbility } from "@lib/privileges";
import React from "react";
import { renderToStaticNodeStream } from "react-dom/server";
import { getFullTemplateByID } from "@lib/templates";
import HTMLDownloadFile from "@components/myforms/HTMLDownload";
import BaseApp from "@pages/_app";
import { Router } from "next/router";
import { checkOne } from "@lib/cache/flags";
import { connectToDynamo } from "@lib/integration/dynamodbConnector";

/**
 * Handler for the retrieval API route. This function simply calls the relevant function depending on the HTTP method
 * @param req - The HTTP request object
 * @param res - The HTTP response object
 */

const allowedMethods = ["GET"];

const handler = async (req: NextApiRequest, res: NextApiResponse, props: MiddlewareProps) => {
  // Is this feature / endpoint active
  const vaultActive = await checkOne("vault");
  if (!vaultActive) return res.status(404).json({ error: "Vault not active" });

  const formID = req.query.form;
  const submissionName = req.query.submission;

  const { session } = props as WithRequired<MiddlewareProps, "session">;

  const userEmail = session.user.email;
  if (userEmail === null)
    throw new Error(
      `User does not have an associated email address: ${JSON.stringify(session.user)} `
    );

  if (!formID || Array.isArray(formID) || !submissionName || Array.isArray(submissionName))
    return res.status(400).json({ error: "Bad Request" });

  try {
    const fullFormTemplate = await getFullTemplateByID(
      createAbility(session.user.privileges),
      formID
    );

    if (fullFormTemplate === null) return res.status(404).json({ error: "Form Not Found" });

    const documentClient = connectToDynamo();

    const getItemsDbParams: GetCommandInput = {
      TableName: "Vault",
      Key: {
        FormID: formID,
        NAME_OR_CONF: `NAME#${submissionName}`,
      },
      ExpressionAttributeNames: {
        "#Name": "Name",
      },
      ProjectionExpression: "SubmissionID,FormSubmission,ConfirmationCode,#Name,CreatedAt",
    };
    const queryCommand = new GetCommand(getItemsDbParams);

    const response = await documentClient.send(queryCommand);

    // If the SubmissionID does not exist return a 404
    if (response.Item === undefined) return res.status(404).json({ error: "Submission not Found" });

    const { formResponse, createdAt, confirmReceiptCode, responseID, submissionID } = ((
      vaultResult
    ): {
      formResponse: Responses;
      createdAt: number;
      confirmReceiptCode: string;
      responseID: string;
      submissionID: string;
    } => {
      return {
        formResponse: JSON.parse(vaultResult.FormSubmission),
        createdAt: vaultResult.CreatedAt,
        confirmReceiptCode: vaultResult.ConfirmationCode,
        // Note: "name" is not a reserved word in JS, incase anyone else was worried
        // https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Lexical_grammar#reserved_words
        responseID: vaultResult.Name,
        submissionID: vaultResult.SubmissionID,
      };
    })(response.Item);

    const pageProps = {
      formResponse,
      formTemplate: fullFormTemplate.form,
      confirmReceiptCode,
      // submissionID,
      responseID,
      createdAt,
      pathname: "/",
      query: {},
      _nextI18Next: {
        initialLocale: "en",
        ns: ["my-forms", "common"],
        userConfig: {
          i18n: {
            defaultLocale: "en",
            locales: ["en", "fr"],
          },
          returnNull: false,
          localePath: "./public/static/locales",
          reloadOnPrerender: true,
          default: {
            i18n: {
              defaultLocale: "en",
              locales: ["en", "fr"],
            },
            returnNull: false,
            localePath: "./public/static/locales",
            reloadOnPrerender: true,
          },
        },
      },
      isHTMLFileDownload: true,
    };

    res.setHeader("Content-Disposition", `attachment; filename=${responseID}.html`);

    const stream = renderToStaticNodeStream(
      <>
        <html lang="en">
          {/* eslint-disable-next-line @next/next/no-head-element */}
          <head>
            <meta charSet="utf-8" />
            <title>{`${fullFormTemplate.form.titleEn} - ${fullFormTemplate.form.titleFr}`}</title>
            <style dangerouslySetInnerHTML={{ __html: css }}></style>
          </head>
          <body>
            <BaseApp
              pageProps={pageProps}
              Component={HTMLDownloadFile as unknown as NextComponentType<NextPageContext>}
              router={{} as unknown as Router}
              __N_SSG={true}
            />
          </body>
        </html>
      </>
    );

    await renderPageToClient(res, stream).catch((err) => {
      logMessage.error(err);
      throw new Error(
        `Download Form Response could not be rendered for response name: ${responseID},submissionID: ${submissionID}, formID:${formID}`
      );
    });

    logMessage.info(
      `user:${session?.user.email} retrieved form responses for response name: ${responseID}, submissionID: ${submissionID}, form ID: ${formID}`
    );

    // Setting last downloaded by on Vault Submission

    await updateLastDownloadedBy(responseID, formID, userEmail);
  } catch (error) {
    if (error instanceof AccessControlError) return res.status(403).json({ error: "Forbidden" });
    logMessage.error(error as Error);
    res.status(500).json({ error: "Error on Server Side when fetching form's responses" });
  }
};

/**
 * Sets who last downloaded the Form Submission on the Vault Submission record
 * @param submissionID Submission ID of the form response
 * @param formID Form ID the Submission is for
 * @param email Email address of the user downloading the Submission
 */
async function updateLastDownloadedBy(responseID: string, formID: string, email: string) {
  const documentClient = connectToDynamo();

  const updateCommandInput: UpdateCommandInput = {
    TableName: "Vault",
    Key: {
      FormID: formID,
      NAME_OR_CONF: `NAME#${responseID}`,
    },
    UpdateExpression: "SET LastDownloadedBy = :email",
    ExpressionAttributeValues: {
      ":email": email,
    },
    ReturnValues: "NONE",
  };
  return await documentClient.send(new UpdateCommand(updateCommandInput));
}

/**
 * Fuction that renders and sends the stream html output to the client.
 * @param res NextJS response
 * @param stream NodeJS Readable stream
 *
 */
async function renderPageToClient(res: NextApiResponse, stream: NodeJS.ReadableStream) {
  return new Promise<void>((resolve, reject) => {
    stream
      .on("error", (err) => reject(err))
      .pipe(res)
      .on("finish", () => {
        logMessage.debug("Sucessfully rendered html and streamed html to client");
        resolve();
      })
      .on("error", (err) => reject(err));
  });
}

